import React, { useState,useEffect } from "react"
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import {getWeb3} from "../getWeb3"
import map from "../artifacts/deployments/map.json"
import {getEthereum} from "../getEthereum"
import {GlobalContext} from '../context/GlobalState';


const Transactions =  () => {
  const[transactions, setTransactions] = useState([]);
  const {currencies} = useContext(GlobalContext);

  useEffect(() => {
    let loadContract = async (chain, contractName, address) => {
      // Load a deployed contract instance into a web3 contract object
      const {web3} = this.state
      /*
      // Get the address of the most recent deployment from the deployment map
      let address
      try {
          address = map[chain][contractName][0]
      } catch (e) {
          console.log(`Couldn't find any deployed contract "${contractName}" on the chain "${chain}".`)
          return undefined
      }*/

      // Load the artifact with the specified address
      let contractArtifact
      try {
          contractArtifact = await import(`../artifacts/deployments/${chain}/${address}.json`)
      } catch (e) {
          console.log(`Failed to load contract artifact "../artifacts/deployments/${chain}/${address}.json"`)
          return undefined
      }

      return new web3.eth.Contract(contractArtifact.abi, address)
  }

    async function fetchTransactions(){
    const web3 = await getWeb3()

    // Try and enable accounts (connect metamask)
    const ethereum = await getEthereum()
    try {
        //const ethereum = await getEthereum()
        ethereum.enable()
        console.log(`Metamask`)

        const accounts = await ethereum.request({ method: 'eth_requestAccounts' });

        // Get the current chain id
        const chainid = parseInt(await web3.eth.getChainId())

        // <=42 to exclude Kovan, <42 to include kovan
        if (this.state.chainid < 42) {
          // Wrong Network!
          return
      }
      console.log(this.state.chainid)

      var _chainID = 0;
      if (this.state.chainid === 42){
          _chainID = 42;
      }
      if (this.state.chainid === 1337){
          _chainID = "dev"
      }
      console.log(_chainID)
      const ssbridge = await this.loadContract(_chainID,"SourceDomainSideBridge", "0xD904b21D46603e2B6C606f401C412fE413DcAB74")
      let root = await ssbridge.methods.get_deposit_root().call()
      if (!ssbridge) {
          return
      }
      let events = await ssbridge.getPastEvents("allEvents", { fromBlock: 1})
      //const ssbridgeEvents = await ssbridge.getPastEvents("allEvents", { fromBlock: 1}).then(console.log);
      console.log("Events")
      console.log(events[0]['returnValues'])
      console.log(root)
      setTransactions(events)

    } catch (e) {
        console.log(`Could not enable accounts. Interaction with contracts not available.
        Use a modern browser with a Web3 plugin to fix this issue.`)
        console.log(e)
    }
  }
  fetchTransactions()
  }, [transactions])

  return (

    <div>
        <h2>Recent Transactions</h2>

        <table className="table table-striped">
          <thead>
              <tr>
                <th scope="col">Token</th>
                <th scope="col">Destination Address</th>
                <th scope="col">Amount</th>
                <th scope="col">Status</th>
              </tr>
          </thead>
          <tbody>
              {transactions.map((transaction,index)=>(
                  <tr key={index}>
                    {GetCurrencyName(currencies,transaction)}
                    <td>{transaction[1]}</td>
                    <td>{transaction[2]}</td>
                    <td>Pending Bounty</td>
                  </tr>
              ))}
          </tbody>
        </table>
    </div>
  )
}

function GetCurrencyName(currencies,transaction) {
  return (
    <td>{currencies.find(currency => currency.address === transaction[0]).name}</td>
  );
}

export default Transactions
