import React from "react"
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';

const Transactions =  ({transactions,currencies}) => {

  return (

    <div>
        <h2>Recent Transactions</h2>

        <table className="table table-striped">
        <thead>
            <tr>
            <th scope="col">Token</th>
            <th scope="col">Destination Address</th>
            <th scope="col">Amount</th>
            <th scope="col">Status</th>
            </tr>
        </thead>
        <tbody>
            {transactions.map((transaction,index)=>(
                <tr key={index}>
                {GetCurrencyName(currencies,transaction)}
                <td>{transaction[1]}</td>
                <td>{transaction[2]}</td>
                <td>Pending Bounty</td>
            </tr>
            ))}
        </tbody>
        </table>
    </div>
  )
}

function GetCurrencyName(currencies,transaction) {
  return (
    <td>{currencies.find(currency => currency.address === transaction[0]).name}</td>
  );
}

export default Transactions
