import React, { useState, useContext } from "react"
import {GlobalContext} from '../context/GlobalState';

const Form = () => {
  const [token, setToken] = useState('');
  const [amount, setAmount] = useState(0);
  const [destination, setDestination] = useState('');
  const {currencies} = useContext(GlobalContext);


  return (
    <form className="needs-validation p-3" onSubmit={(e) => this.setWithdrawal(e)} noValidate>
      <div className="mb-3">
          <label htmlFor="Token" className="form-label">Token</label>
          <select className="form-select" onChange={(e) => setToken(e.target.value)} required>
          {currencies.map((currency)=>(
                <option key={currency.key} value={currency.address}>{currency.name}</option>
            ))}
          </select>
       </div>
       <div className="mb-3">
          <label htmlFor="Amount" className="form-label">Amount</label>
          <input type="text" className="form-control" name="amountInput" placeholder="" value={amount}
          onChange={(e) => setAmount(e.target.value)} required/>
       </div>
       <div className="mb-3">
          <label htmlFor="Destination" className="form-label">Destination Address</label>
          <input type="text" className="form-control"  name="destinationInput" placeholder="" value={destination}
          onChange={(e) => setDestination(e.target.value)} required/>
       </div>
       <button type="submit" className="btn btn-primary">Send</button>
    </form>
  )
}

export default Form
