import React, { useState } from "react"


import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';


const Form = ({currencies}) => {
  const [token, setToken] = useState('');
  const [amount, setAmount] = useState(0);
  const [destination, setDestination] = useState('');

  return (
    <form className="needs-validation p-3" noValidate>
      <div className="mb-3">
          <label htmlFor="Token" className="form-label">Token</label>
          <select className="form-select">
          {currencies.map((currency)=>(
                <option key={currency.key} value={currency.address}>{currency.name}</option>
            ))}
          </select>
       </div>
       <div className="mb-3">
          <label htmlFor="Amount" className="form-label">Amount</label>
          <input type="text" className="form-control" id="amountInput" name="amountInput" placeholder="" value={amount}
          onChange={(e) => setAmount({amount: e.target.value})} required/>
       </div>
       <div className="mb-3">
          <label htmlFor="Destination" className="form-label">Destination Address</label>
          <input type="text" className="form-control" id="destinationInput" name="destinationInput" placeholder="" value={destination}
          onChange={(e) => setDestination({destination: e.target.value})} required/>
       </div>
       <button type="submit" className="btn btn-primary">Send</button>
    </form>
  )
}

export default Form
